	private void jButton1ActionPerformed(java.awt.event.ActionEvent evt)  {
		// TODO add your handling code here:
		String name=this.jTextField1.getText();
		String password=this.jPasswordField1.getText();
		try{
		if(this.jRadioButton1.isSelected()){
			Admin admin=Admin.ALogin(name,password);
			JOptionPane.showMessageDialog(this,admin.getAdmin()+ "登陆成功");
			new AdminMenu(admin).setVisible(true);
			this.dispose();
		}else if(this.jRadioButton2.isSelected()){
			Student student=Student.Login(name,password);
		    JOptionPane.showMessageDialog(this, student.getName()+"登陆成功");
		    new StudentMenu(student).setVisible(true);
		    this.dispose();
		}else if(this.jRadioButton3.isSelected()){
		    Teacher teacher=Teacher.TLogin(name, password);
		    JOptionPane.showMessageDialog(this, teacher.getTeacher()+"登陆成功");
		    new TeacherMenu(teacher).setVisible(true);
		    this.dispose();} 
		   }catch (Exception e) {
			// TODO Auto-generated catch block
			JOptionPane.showMessageDialog(this, e.getMessage());
		}
	}

