	private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {
		String username = this.jTextField1.getText();
		String password = this.jPasswordField1.getText();
		DatabaseConnect dc = new DatabaseConnect();
		try {
			if (this.jComboBox1.getSelectedItem().equals("学生")) {
				Student s = Student.login(Integer.parseInt(username), password,
						dc.getStat());
				new StudetFrame(s).setVisible(true);

			}
			if (this.jComboBox1.getSelectedItem().equals("老师")) {
				Teacher t = Teacher.login(Integer.parseInt(username), password,
						dc.getStat());
				new TeacherFrame(t).setVisible(true);
			}
			if (this.jComboBox1.getSelectedItem().equals("管理员")) {
				Admin a = Admin.login(username, password, dc.getStat());
				new AdminFrame(a).setVisible(true);
			}
		} catch (Exception e) {
			JOptionPane.showMessageDialog(this, e.getMessage());
		}
	}

