	private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {
		// TODO add your handling code here:
		JFileChooser jfc=new JFileChooser();
		jfc.showOpenDialog(this);
		String fname=jfc.getSelectedFile().getAbsolutePath();
		System.out.println(fname);
		Image img=new ImageIcon(fname).getImage().getScaledInstance(this.jLabel1.getWidth(), this.jLabel1.getHeight(), Image.SCALE_SMOOTH);
		this.jLabel1.setIcon(new ImageIcon(img));
	}//主要的代码

