	private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {
		String name = this.jTextField1.getText();
		String ps = this.jPasswordField1.getText();
		teacher = Teacher.login(name, ps);
		if (teacher != null) {
			new MainView(teacher).setVisible(true);
			this.dispose();
		} else
			JOptionPane.showMessageDialog(this, "’ÀªßªÚ√‹¬Î¥ÌŒÛ");
	}

